    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String sign = "insert into returnbook(username,bookID) values (?,?)";
        try {
         statmnt = connect.prepareStatement(sign);
         statmnt.setString(1,jTextField1.getText());
         statmnt.setString(2,jTextField2.getText());
         
        statmnt.execute();
           JOptionPane.showMessageDialog(null, " Thank you for returning your book, we will check it out of your account.");}
     catch(Exception e) {
         JOptionPane.showMessageDialog(null, e);}
     
                   
        
    }//GEN-LAST:event_jButton1ActionPerformed

